def deduce_vigenere_key(ciphertext, key_length):
    key = [""] * key_length
    english_freq = "ETAOINSHRDLCUMWFGYPBVKJXQZ"

    for i in range(key_length):
        segment = ciphertext[i::key_length]

        freq = [0] * 26
        for char in segment:
            freq[ord(char) - ord("A")] += 1

        best_shift = 0
        best_correlation = float("-inf")
        for shift in range(26):
            correlation = sum(
                freq[(j + shift) % 26] * (26 - english_freq.index(chr(j + ord("A"))))
                for j in range(26)
            )
            if correlation > best_correlation:
                best_correlation = correlation
                best_shift = shift
        print(best_shift)
        key[i] = chr((26 - best_shift) % 26 + ord("A"))

    return "".join(key)


# Example usage
key = "CODEWARS"
ciphertext = ""
print(deduce_vigenere_key(ciphertext, len(key)))  # Should output CODEWARS
